import './App.css';
import axios from 'axios';
import React, { useState, useEffect } from "react";

function App() {
  const [weather, setWeather] = useState([])

  useEffect(() => {
    var location = `<input type="text" placeholder="Enter Your City" id="location" />
    <button onClick="location()">Search</button>`
    axios.get(`https://api.openweathermap.org/data/2.5/weather?q=karachi&appid=1d0329a9cf37be737aedaf5d50dddd68&units=metric`)
      .then(res => {
        const newWeather = res.data;
        setWeather(newWeather);
      });


    return () => {
      console.log("Unloaded")
    };
  },
    []);

  return (
    <div>


      <h1>{weather?.main?.temp}<sup>o</sup>C</h1>
      <h1>{weather?.main?.humidity}</h1>
      <h1>{weather?.main?.pressure}</h1>
      <h1>{weather?.coord?.lon}</h1>
      {/* <input type="text" placeholder="Enter Your City"/> */}

    </div>
  )
}
function Example() {
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  return (
    <>
      <Button variant="primary" onClick={handleShow}>
        Launch demo modal
      </Button>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleClose}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}





export default App;
