import './App.css';
import axios from 'axios';
import React, { useState, useEffect } from "react";

function App() {
  const [weather, setWeather] = useState([])

  useEffect(() => {

    var location = prompt("Enter City Name")
    axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${location}&appid=1d0329a9cf37be737aedaf5d50dddd68&units=metric`)
      .then(res => {
        const newWeather = res.data;
        setWeather(newWeather);
      });


    return () => {
      console.log("Unloaded")
    };
  },
    []);

  return (
    <div>


      <h1>{weather?.timezone?.name}</h1>
      <h1>{weather?.main?.temp}<sup>o</sup>C</h1>
      <h1>{weather?.main?.humidity}</h1>
      <h1>{weather?.main?.pressure}</h1>
      {/* <input type="text" placeholder="Enter Your City"/> */}

    </div>
  )
}




export default App;
